import React, { useState, useEffect } from 'react';
import {
  Box,
  Typography,
  Card,
  CardContent,
  Grid,
  Button,
  Alert,
  CircularProgress,
  Chip,
} from '@mui/material';
import {
  AccountBalanceWallet,
  TrendingUp,
  Storage,
  Speed,
  Refresh,
} from '@mui/icons-material';
import { useWallet } from '../contexts/WalletContext';
import api from '../utils/api';

const HomePage = () => {
  const { isConnected, isAnvilNetwork, account } = useWallet();
  const [stats, setStats] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Ëé∑ÂèñÁ≥ªÁªüÁªüËÆ°Êï∞ÊçÆ
  const fetchStats = async () => {
    try {
      setLoading(true);
      setError(null);
      const response = await api.get('/api/stats');
      setStats(response.data);
    } catch (err) {
      console.error('Ëé∑ÂèñÁªüËÆ°Êï∞ÊçÆÂ§±Ë¥•:', err);
      setError('Ëé∑ÂèñÁªüËÆ°Êï∞ÊçÆÂ§±Ë¥•ÔºåËØ∑Ê£ÄÊü•ÂêéÁ´ØÊúçÂä°ÊòØÂê¶Ê≠£Â∏∏ËøêË°å');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchStats();
  }, []);

  const StatCard = ({ title, value, icon, color = 'primary' }) => (
    <Card elevation={2}>
      <CardContent>
        <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
          <Box
            sx={{
              p: 1,
              borderRadius: 2,
              backgroundColor: `${color}.light`,
              color: `${color}.contrastText`,
              mr: 2,
            }}
          >
            {icon}
          </Box>
          <Typography variant="h6" component="div">
            {title}
          </Typography>
        </Box>
        <Typography variant="h4" component="div" sx={{ fontWeight: 'bold' }}>
          {value}
        </Typography>
      </CardContent>
    </Card>
  );

  return (
    <Box>
      {/* È°µÈù¢Ê†áÈ¢ò */}
      <Box sx={{ mb: 4 }}>
        <Typography variant="h4" component="h1" gutterBottom>
          üè† Âå∫ÂùóÈìæÊï∞ÊçÆÁ¥¢ÂºïÂô®
        </Typography>
        <Typography variant="subtitle1" color="text.secondary">
          ÂÆûÊó∂Á¥¢ÂºïAnvilÊú¨Âú∞ÁΩëÁªúÁöÑERC20 Transfer‰∫ã‰ª∂
        </Typography>
      </Box>

      {/* ÁΩëÁªúÁä∂ÊÄÅÊèêÁ§∫ */}
      <Box sx={{ mb: 3 }}>
        {!isConnected ? (
          <Alert severity="info" sx={{ mb: 2 }}>
            <Typography variant="body1">
              ËØ∑ËøûÊé•MetaMaskÈí±ÂåÖ‰ª•Ëé∑ÂæóÂÆåÊï¥ÂäüËÉΩ‰ΩìÈ™å
            </Typography>
          </Alert>
        ) : !isAnvilNetwork ? (
          <Alert severity="warning" sx={{ mb: 2 }}>
            <Typography variant="body1">
              ËØ∑ÂàáÊç¢Âà∞AnvilÊú¨Âú∞ÁΩëÁªú (Chain ID: 31337) ‰ª•‰ΩøÁî®ÂÆåÊï¥ÂäüËÉΩ
            </Typography>
          </Alert>
        ) : (
          <Alert severity="success" sx={{ mb: 2 }}>
            <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
              <Typography variant="body1">
                Â∑≤ËøûÊé•Âà∞AnvilÊú¨Âú∞ÁΩëÁªú
              </Typography>
              <Chip
                label={`Ë¥¶Êà∑: ${account?.slice(0, 6)}...${account?.slice(-4)}`}
                size="small"
                color="success"
                variant="outlined"
              />
            </Box>
          </Alert>
        )}
      </Box>

      {/* ÁªüËÆ°Êï∞ÊçÆÂç°Áâá */}
      <Box sx={{ mb: 4 }}>
        <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
          <Typography variant="h5" component="h2">
            üìä Á≥ªÁªüÁªüËÆ°
          </Typography>
          <Button
            startIcon={<Refresh />}
            onClick={fetchStats}
            disabled={loading}
            variant="outlined"
            size="small"
          >
            Âà∑Êñ∞Êï∞ÊçÆ
          </Button>
        </Box>

        {loading ? (
          <Box sx={{ display: 'flex', justifyContent: 'center', py: 4 }}>
            <CircularProgress />
          </Box>
        ) : error ? (
          <Alert severity="error">
            {error}
          </Alert>
        ) : (
          <Grid container spacing={3}>
            <Grid item xs={12} sm={6} md={3}>
              <StatCard
                title="ÊÄª‰∫§ÊòìÊï∞"
                value={stats?.totalTransactions?.toLocaleString() || '0'}
                icon={<TrendingUp />}
                color="primary"
              />
            </Grid>
            <Grid item xs={12} sm={6} md={3}>
