  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Pagination,
  Alert,
  CircularProgress,
  Chip,
  InputAdornment,
  IconButton,
} from '@mui/material';
import {
  Search,
  Clear,
  OpenInNew,
  TrendingUp,
  TrendingDown,
