    if (!address || !address.match(/^0x[a-fA-F0-9]{40}$/)) {
      setError('ËØ∑ËæìÂÖ•ÊúâÊïàÁöÑ‰ª•Â§™ÂùäÂú∞ÂùÄ');
      return;
    }

    try {
      setLoading(true);
      setError(null);

      // Ëé∑Âèñ‰∫§ÊòìËÆ∞ÂΩï
      const response = await api.get(`/api/transactions/${address}`, {
        params: {
          page: pageNum,
          limit: pageSize,
        },
      });

      setTransactions(response.data.transactions);
      setTotalPages(Math.ceil(response.data.total / pageSize));
      setPage(pageNum);

      // Ëé∑Âèñ‰∫§ÊòìÊëòË¶Å
      const summaryResponse = await api.get(`/api/transactions/${address}/summary`);
      setSummary(summaryResponse.data);

    } catch (err) {
      console.error('ÊêúÁ¥¢‰∫§ÊòìÂ§±Ë¥•:', err);
      if (err.response?.status === 404) {
        setError('Êú™ÊâæÂà∞ËØ•Âú∞ÂùÄÁöÑ‰∫§ÊòìËÆ∞ÂΩï');
      } else {
        setError('ÊêúÁ¥¢Â§±Ë¥•ÔºåËØ∑Ê£ÄÊü•ÁΩëÁªúËøûÊé•ÊàñÂêéÁ´ØÊúçÂä°');
      }
      setTransactions([]);
      setSummary(null);
    } finally {
      setLoading(false);
    }
  };

  // Â§ÑÁêÜÊêúÁ¥¢
  const handleSearch = () => {
    if (searchAddress.trim()) {
      searchTransactions(searchAddress.trim(), 1);
    }
  };

  // Â§ÑÁêÜÂàÜÈ°µ
  const handlePageChange = (event, newPage) => {
    if (searchAddress.trim()) {
      searchTransactions(searchAddress.trim(), newPage);
    }
  };

  // Ê∏ÖÁ©∫ÊêúÁ¥¢
  const handleClear = () => {
    setSearchAddress('');
    setTransactions([]);
    setSummary(null);
    setError(null);
    setPage(1);
    setTotalPages(0);
  };

  // ‰ΩøÁî®ÂΩìÂâçËøûÊé•ÁöÑË¥¶Êà∑ÊêúÁ¥¢
  const searchMyTransactions = () => {
    if (account) {
      setSearchAddress(account);
      searchTransactions(account, 1);
    }
  };

  // Ê†ºÂºèÂåñÂú∞ÂùÄÊòæÁ§∫
  const formatAddress = (address) => {
    return `${address.slice(0, 6)}...${address.slice(-4)}`;
  };

  // Ê†ºÂºèÂåñÈáëÈ¢ùÊòæÁ§∫
  const formatAmount = (amount) => {
    const value = parseFloat(amount);
    if (value === 0) return '0';
    if (value < 0.0001) return value.toExponential(2);
    return value.toLocaleString(undefined, { maximumFractionDigits: 6 });
  };

  // Âà§Êñ≠‰∫§ÊòìÁ±ªÂûãÔºàÂèëÈÄÅ/Êé•Êî∂Ôºâ
  const getTransactionType = (tx, address) => {
    const lowerAddress = address.toLowerCase();
    if (tx.from_address.toLowerCase() === lowerAddress) {
      return 'sent';
    } else if (tx.to_address.toLowerCase() === lowerAddress) {
      return 'received';
    }
    return 'unknown';
  };

  return (
    <Box>
      {/* È°µÈù¢Ê†áÈ¢ò */}
      <Box sx={{ mb: 4 }}>
        <Typography variant="h4" component="h1" gutterBottom>
          üìã ‰∫§ÊòìËÆ∞ÂΩïÊü•ËØ¢
        </Typography>
        <Typography variant="subtitle1" color="text.secondary">
          Êü•ËØ¢ÊåáÂÆöÂú∞ÂùÄÁöÑERC20‰ª£Â∏ÅËΩ¨Ë¥¶ËÆ∞ÂΩï
        </Typography>
      </Box>

      {/* ÊêúÁ¥¢Âå∫Âüü */}
      <Card elevation={2} sx={{ mb: 3 }}>
        <CardContent>
          <Box sx={{ display: 'flex', gap: 2, alignItems: 'flex-start' }}>
            <TextField
              fullWidth
              label="‰ª•Â§™ÂùäÂú∞ÂùÄ"
              placeholder="ËæìÂÖ•Ë¶ÅÊü•ËØ¢ÁöÑ‰ª•Â§™ÂùäÂú∞ÂùÄ (0x...)"
              value={searchAddress}
              onChange={(e) => setSearchAddress(e.target.value)}
              onKeyPress={(e) => e.key === 'Enter' && handleSearch()}
              InputProps={{
                startAdornment: (
                  <InputAdornment position="start">
                    <Search />
                  </InputAdornment>
                ),
                endAdornment: searchAddress && (
                  <InputAdornment position="end">
                    <IconButton onClick={handleClear} size="small">
                      <Clear />
                    </IconButton>
                  </InputAdornment>
                ),
              }}
            />
            <Button
              variant="contained"
              onClick={handleSearch}
              disabled={loading || !searchAddress.trim()}
              sx={{ minWidth: 100 }}
            >
              ÊêúÁ¥¢
            </Button>
            {isConnected && account && (
              <Button
                variant="outlined"
                onClick={searchMyTransactions}
                disabled={loading}
                sx={{ minWidth: 120 }}
              >
                Êü•ËØ¢ÊàëÁöÑ‰∫§Êòì
              </Button>
            )}
          </Box>
        </CardContent>
      </Card>

      {/* ÈîôËØØÊèêÁ§∫ */}
      {error && (
        <Alert severity="error" sx={{ mb: 3 }}>
          {error}
        </Alert>
      )}

      {/* ‰∫§ÊòìÊëòË¶Å */}
      {summary && (
        <Card elevation={1} sx={{ mb: 3 }}>
          <CardContent>
            <Typography variant="h6" gutterBottom>
              üìä ‰∫§ÊòìÊëòË¶Å
            </Typography>
            <Box sx={{ display: 'flex', gap: 3, flexWrap: 'wrap' }}>
              <Chip
                icon={<TrendingUp />}
                label={`ÊÄª‰∫§ÊòìÊï∞: ${summary.totalTransactions}`}
                color="primary"
                variant="outlined"
              />
              <Chip
                icon={<TrendingUp />}
                label={`ÂèëÈÄÅ‰∫§Êòì: ${summary.sentTransactions}`}
                color="error"
                variant="outlined"
              />
              <Chip
                icon={<TrendingDown />}
                label={`Êé•Êî∂‰∫§Êòì: ${summary.receivedTransactions}`}
                color="success"
                variant="outlined"
              />
            </Box>
          </CardContent>
        </Card>
      )}

      {/* Âä†ËΩΩÁä∂ÊÄÅ */}
      {loading && (
        <Box sx={{ display: 'flex', justifyContent: 'center', py: 4 }}>
          <CircularProgress />
        </Box>
      )}

      {/* ‰∫§ÊòìÂàóË°® */}
      {!loading && transactions.length > 0 && (
        <Card elevation={2}>
          <CardContent sx={{ p: 0 }}>
            <TableContainer component={Paper} elevation={0}>
              <Table>
                <TableHead>
                  <TableRow sx={{ backgroundColor: 'grey.50' }}>
                    <TableCell>‰∫§ÊòìÂìàÂ∏å</TableCell>
                    <TableCell>Á±ªÂûã</TableCell>
                    <TableCell>ÂèëÈÄÅÊñπ</TableCell>
                    <TableCell>Êé•Êî∂Êñπ</TableCell>
                    <TableCell align="right">ÈáëÈ¢ù</TableCell>
                    <TableCell>‰ª£Â∏ÅÂú∞ÂùÄ</TableCell>
                    <TableCell>Êó∂Èó¥</TableCell>
                    <TableCell>Êìç‰Ωú</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {transactions.map((tx, index) => {
                    const txType = getTransactionType(tx, searchAddress);
                    return (
                      <TableRow key={index} hover>
                        <TableCell>
                          <Typography variant="body2" sx={{ fontFamily: 'monospace' }}>
                            {formatAddress(tx.transaction_hash)}
                          </Typography>
                        </TableCell>
                        <TableCell>
                          <Chip
                            size="small"
                            label={txType === 'sent' ? 'ÂèëÈÄÅ' : 'Êé•Êî∂'}
                            color={txType === 'sent' ? 'error' : 'success'}
                            variant="outlined"
                          />
                        </TableCell>
                        <TableCell>
                          <Typography variant="body2" sx={{ fontFamily: 'monospace' }}>
                            {formatAddress(tx.from_address)}
                          </Typography>
                        </TableCell>
                        <TableCell>
                          <Typography variant="body2" sx={{ fontFamily: 'monospace' }}>
                            {formatAddress(tx.to_address)}
                          </Typography>
                        </TableCell>
                        <TableCell align="right">
                          <Typography variant="body2" sx={{ fontWeight: 'bold' }}>
                            {formatAmount(tx.amount)}
                          </Typography>
                        </TableCell>
                        <TableCell>
                          <Typography variant="body2" sx={{ fontFamily: 'monospace' }}>
                            {formatAddress(tx.token_address)}
                          </Typography>
                        </TableCell>
                        <TableCell>
                          <Typography variant="body2">
                            {moment(tx.timestamp).format('MM-DD HH:mm:ss')}
                          </Typography>
                        </TableCell>
                        <TableCell>
                          <IconButton
                            size="small"
                            onClick={() => window.open(`#/tx/${tx.transaction_hash}`, '_blank')}
                          >
                            <OpenInNew fontSize="small" />
                          </IconButton>
                        </TableCell>
                      </TableRow>
                    );
                  })}
                </TableBody>
              </Table>
            </TableContainer>

            {/* ÂàÜÈ°µ */}
            {totalPages > 1 && (
              <Box sx={{ display: 'flex', justifyContent: 'center', p: 3 }}>
                <Pagination
                  count={totalPages}
                  page={page}
                  onChange={handlePageChange}
                  color="primary"
                  showFirstButton
                  showLastButton
                />
              </Box>
            )}
          </CardContent>
        </Card>
      )}

      {/* Êó†Êï∞ÊçÆÊèêÁ§∫ */}
      {!loading && !error && transactions.length === 0 && searchAddress && (
        <Card elevation={1}>
          <CardContent sx={{ textAlign: 'center', py: 6 }}>
            <Typography variant="h6" color="text.secondary" gutterBottom>
              üì≠ ÊöÇÊó†‰∫§ÊòìËÆ∞ÂΩï
            </Typography>
            <Typography variant="body2" color="text.secondary">
              ËØ•Âú∞ÂùÄÊöÇÊó†ERC20‰ª£Â∏ÅËΩ¨Ë¥¶ËÆ∞ÂΩïÔºåÊàñËÄÖÊï∞ÊçÆËøòÂú®Á¥¢Âºï‰∏≠
            </Typography>
          </CardContent>
        </Card>
      )}
    </Box>
  );
};

export default TransactionsPage;
